PartialViews.View:
@using Umbraco.Cms.Core.Models.PublishedContent
@using Umbraco.Extensions
@inherits Umbraco.Cms.Web.Common.Views.UmbracoViewPage

@{
    var site = (IPublishedContent)Umbraco.ContentAtRoot().FirstOrDefault();
    var children = site.Children().Where(x => x.IsVisible());
    var siblings = site.Siblings();
}

<!-- create your responsive navbar -->
<nav class=" navbar-expand-lg navbar navbar-light bg-white" id="myNavbar">
    <!-- next is where your logo goes -->
    <img src="/wizard.jpg?width=60&height=60" alt="MediaWizards Logo" />
    <a href="@site.Url()" class="navbar-brand" title="@site.Value("siteName")">@site.Value("siteName")</a>
    <!-- next is your mobile burger menu toggle -->
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#mainNav" aria-controls="mainNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
    </button>

    <!-- now we go to the main bit of the nav -->
    <div class="collapse navbar-collapse" id="mainNav">
        <ul class="navbar-nav me-auto mb-2 mb-lg-0">

            <!-- un comment the following line if you want to add the root node name to the top navigation
            <li class="nav-item px-4"><a class="nav-link @Html.Raw(Model.Id == site.Id ? "navi-link--active" : "")" href="@site.Url()">@site.Name</a></li>-->

            @foreach (var item in children)
            {
                if (item.Children().Any(x => x.IsVisible()) && !item.Value<bool>("hideChildrenFromNav"))
                {
                    <li class="nav-item px-4 dropdown">
                        <a class="nav-link dropdown-toggle @(item.IsAncestorOrSelf(Model) ? "nav-link--active" : null)" href="#" id="@($"{item.Name.Replace(" ", "")}Dropdown")" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">@item.Name</a>
                        <div class="dropdown-menu" aria-labelledby="@($"{item.Name.Replace(" ", "")}Dropdown")">
                            @foreach (var child in item.Children().Where(x => x.IsVisible()))
                            {
                                <a class="dropdown-item" href="@child.Url()">@child.Name</a>
                            }
                        </div>
                    </li>
                }
                else
                {
                    <li class="nav-item px-4"><a class="nav-link @(item.IsAncestorOrSelf(Model) ? "nav-link--active" : null)" href="@item.Url()">@item.Name</a></li>
                }
            }
            @foreach (var item in siblings)
            {
                if (item.Children().Any(x => x.IsVisible()) && !item.Value<bool>("hideChildrenFromNav"))
                {
                    <li class="nav-item px-4 dropdown">
                        <a class="nav-link dropdown-toggle @(item.IsAncestorOrSelf(Model) ? "nav-link--active" : null)" href="#" id="@($"{item.Name.Replace(" ", "")}Dropdown")" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">@item.Name</a>
                        <div class="dropdown-menu" aria-labelledby="@($"{item.Name.Replace(" ", "")}Dropdown")">
                            @foreach (var child in item.Children().Where(x => x.IsVisible()))
                            {
                                <a class="dropdown-item" href="@child.Url()">@child.Name</a>
                            }
                        </div>
                    </li>
                }
                else
                {
                    <li class="nav-item px-4"><a class="nav-link @(item.IsAncestorOrSelf(Model) ? "nav-link--active" : null)" href="@item.Url()">@item.Name</a></li>
                }
            }
        </ul>
    </div>
</nav>

MacroPartialViews.View:
@inherits Umbraco.Cms.Web.Common.Macros.PartialViewMacroPage
@using Umbraco.Cms.Core
@using Umbraco.Cms.Core.Routing
@using Umbraco.Extensions

@inject IPublishedContentQuery PublishedContentQuery
@inject IPublishedValueFallback PublishedValueFallback
@inject IPublishedUrlProvider PublishedUrlProvider


@{
    var startNodeId = Model?.MacroParameters["startNodeId"]; 
    var numberOfPosts = 3;
    if (Model.MacroParameters["numberOfPosts"] != null)
    {
        int.TryParse((string)Model.MacroParameters["numberOfPosts"], out numberOfPosts);
    }

    if (startNodeId != null)
    {
    @* Get the starting page *@
        var startNode = PublishedContentQuery.Content(startNodeId);
        var selection = startNode.DescendantsOrSelf()
            .Where(x => x.IsVisible(PublishedValueFallback) && x.Value<bool>("postType"))
            .OrderByDescending(o=>o.Value("editDate"))
            .ThenByDescending(o => o.CreateDate).Take(numberOfPosts).ToArray();

if (selection.Length > 0)
{
            <div class="sidebar">
                <span class="wiz-forum-title">&nbsp;Latest forum posts</span>
                <ul class="wiz-topics">
                    <li class="wiz-body">
                        @foreach (var item in selection)
                        {
                            var editdate = item.Value<DateTime>("editDate");
                            <ul class="topic">
                                <li class="wiz-topic-content">
                                    <a class="wiz-topic-permalink" href="@item.Url(PublishedUrlProvider)">@item.Value("postTitle")</a>
                                    <span class="wiz-topic-meta">@item.Value("postBody")</span>
                                    <span class="wiz-topic-header">Created by @item.Value("postCreator") on <span class="wiz-topic-permalink">@item.CreateDate.ToLongDateString() at @item.CreateDate.ToLocalTime().ToShortTimeString()</span>
                                        @if (editdate == DateTime.MinValue)
                                        {

                                        }
                                        else
                                        {
                                            <span><br />Latest post @editdate.ToLocalTime().ToLongDateString() at @editdate.ToLocalTime().ToShortTimeString()</span>
                                        }

                                    </span>
                                </li>
                            </ul>
                        }
                    </li>
                </ul>
            </div>
        }
    }
}
